class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
   vector<int> distances(n);
        for (int i = 1; i < n; ++i) {
            distances[i] = distances[i - 1] + 1;
        }

        set<int> indices;
        for (int i = 0; i < n; ++i) {
            indices.insert(i);
        }

        vector<int> results;

        for (const auto& query : queries) {
            int left = query[0];
            int right = query[1];

            auto lower = indices.lower_bound(left + 1);
            auto upper = indices.lower_bound(right);

            indices.erase(lower, upper);

            results.push_back(indices.size() - 1);
        }

        return results;
    }
};
