class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> ans;
        set<pair<int,int>> s;

        for(int i=0; i+1<n; i++) {
            s.insert({i,i+1});
        }

        for(int e=0; e<q.size(); e++) {
            int u=q[e][0];
            int v=q[e][1];
            auto it=s.lower_bound({u,0});
            while (it!=s.end() && it->second < v) {
                u=it->first;
                s.erase(it);
                it=s.lower_bound({u,0});
            }
            // for(auto it: s) {
            //     cout<<it.first<<" "<<it.second<<endl;
            // } cout<<endl;
            ans.push_back(s.size());
        }

        return ans;
    }
};
