class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> adj(n,0);
        for(int i=1;i<n;i++){
            adj[i]=adj[i-1]+1;
        }
        set<int> p;
        for(int i=0;i<n;i++) p.insert(i);
        vector<int> ans;
        for(auto q:queries){
            int l=q[0];
            int r=q[1];
            auto lb=p.lower_bound(l+1);
            auto ub=p.lower_bound(r);
            p.erase(lb,ub);
            ans.push_back(p.size()-1);
        }

        return ans;

    }
};
