class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int>dist(n,0);
        for(int i=1; i<n; i++){
            dist[i] = dist[i-1] + 1;
        }

        set<int>set;
        for(int i=0; i<n; i++) set.insert(i);

        vector<int>result;

        for(const auto& query : queries){
            int l = query[0];
            int r = query[1];

            auto lb = set.lower_bound(l+1);
            auto ub = set.lower_bound(r);

            set.erase(lb, ub);

            result.push_back(set.size() -1);
        }
        return result;
    }
};
