class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& que) {
        set<int> st;
        for(int i = 0; i < n; ++i)st.insert(i);
        vector<int> ans;
        for (const auto& val : que) {
            int r = val[1],l = val[0];
            auto ub = st.lower_bound(r);
            auto lb = st.lower_bound(l + 1);
            st.erase(lb, ub);
            ans.push_back(st.size() - 1);
        }
        return ans;
    }
};
