class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        map<int, int> s;

        for (int i = 0; i < n - 1; ++i) {
            s.emplace(i, i + 1);
        }

        vector<int> result;
        for (auto& q : queries) {
            auto iter = s.find(q[0]);
            if (iter != s.end() && iter->second < q[1]) {
                iter->second = q[1];
                ++iter;
                while (iter != s.end() && iter->first < q[1]) {
                    auto iter2 = iter;
                    ++iter;
                    s.erase(iter2);
                }
            }

            result.push_back(s.size());
        }
        return result;
    }
};
