class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> elements;
        for (int i = 0; i < n; ++i) {
            elements.insert(i);
        }

        vector<int> result;

        // Process each query
        for (const auto& query : queries) {
            int start = query[0];
            int end = query[1];

            // Find the range to remove
            auto it1 = elements.lower_bound(start + 1);
            auto it2 = elements.upper_bound(end - 1);

            // Remove elements in the range [start+1, end-1]
            elements.erase(it1, it2);

            // Save the number of remaining elements
            result.push_back(elements.size()-1);
        }

        return result;
    }
};
