class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> s;
        for(int i=0;i<n;i++){
            s.insert(i);
        }
        vector<int> ans;
        for(int i=0;i<q.size();i++){
            int src=q[i][0],dest=q[i][1];
            auto it1=s.find(src),it2=s.find(dest);
            if(it1!=s.end() && it2!=s.end()){
                ++it1;
                s.erase(it1,it2);
            }
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
