class Solution {
public:
    using pii = pair<int, int>;
    #define ff first
    #define ss second


    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m = q.size();
        vector<int> res(m);
        set<int> st;
        for(int i = 0; i + 1 < n; i++) {
            st.insert(i);
        }
        for(int i = 0; i < m; i++) {
            int u = q[i][0], v = q[i][1];
            auto it = st.lower_bound(u);
            bool add = false;
            while(it != st.end() and (*it) < v) {
                st.erase(it);
                it = st.lower_bound(u);
                add = true;
            }
            if(add) st.insert(u);
            res[i] = st.size();
        }
        return res;
    }
};
