class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        graph = {}

        for u in range(n):
            graph[u] = u - 1

        out = [n - 1]

        for u, v in queries:
            if u in graph and v in graph:
                w = graph[v]

                while w != u:
                    out[-1] -= 1

                    x = w
                    w = graph[w]
                    del graph[x]

                graph[v] = u

            out.append(out[-1])

        return out[:-1]
