class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        vector<int> d(n, 0);
        for (int i = 1; i < n; ++i) {
            d[i] = d[i - 1] + 1;
        }

        set<int> set1;
        for (int i = 0; i < n; ++i) {
            set1.insert(i);
        }

        vector<int> vec;

        for (const auto& qi : q) {
            int l = qi[0];
            int rt = qi[1];

            auto l1 = set1.lower_bound(l + 1);
            auto l2 = set1.lower_bound(rt);
            set1.erase(l1, l2);
            vec.push_back(set1.size() - 1);
        }

        return vec;
    }
};
