class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int numCities, vector<vector<int>>& queries) {
        vector<int> distance(numCities, 0);
        for (int i = 1; i < numCities; ++i) {
            distance[i] = distance[i - 1] + 1;
        }

        set<int> p;
        for (int i = 0; i < numCities; ++i) {
            p.insert(i);
        }

        vector<int> rz;

        for (auto query : queries) {
            int start = query[0];
            int end = query[1];

            auto lowerBound = p.lower_bound(start + 1);
            auto upperBound = p.lower_bound(end);

            p.erase(lowerBound, upperBound);

            rz.push_back(p.size() - 1);
        }

        return rz;
    }
};
