class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int hulu, vector<vector<int>>& q) {
        vector<int> lulu(hulu, 0);
        for (int i = 1; i < hulu; ++i) {
            lulu[i] = lulu[i - 1] + 1;
        }

        set<int> kik;
        for (int i = 0; i < hulu; ++i) {
            kik.insert(i);
        }

        vector<int> pluu;

        for (const auto& uuu : q) {
            int pik = uuu[1];
            int lik = uuu[0];

            auto sluu = kik.lower_bound(lik + 1);
            auto bluu = kik.lower_bound(pik);

            kik.erase(sluu, bluu);

            pluu.push_back(kik.size() - 1);
        }

        return pluu;
    }
};
