class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        int m=q.size(),i;
        map<int,int> u,v;
        for(i=0;i<n-1;i++)
            u[i]=i+1,v[i+1]=i;
        vector<int> ans(m);
        for(i=0;i<m;i++)
        {
            if(u.find(q[i][0])==u.end())
                ;
            else if(u[q[i][0]]<q[i][1])
            {
                auto it=u.find(q[i][0]);
                vector<int> temp;
                while(it!=u.end())
                {
                    if(it->first>=q[i][1])
                        break;
                    temp.push_back(it->first);
                    it++;
                }
                for(auto x:temp)
                u.erase(x);
                u[q[i][0]]=q[i][1];
            }
            ans[i]=u.size();
            // for(auto [x,y]:u)
            //     cout<<i<<" "<<x<<" "<<y<<endl;
        }
        return ans;
    }
};
