class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {

        vector<int>dist(n,0);
        set<int>pos;
        for(int i = 1; i < n; i++){
            //dist from start for each ind
            dist[i] = dist[i-1] + 1;
        }

        //tracks active pos in range
        for(int i = 0; i < n; i++)
            pos.insert(i);

        vector<int>res;

        for(auto it : queries){

            int row = it[0];
            int col = it[1];

            auto lb = pos.lower_bound(row+1);
            auto ub = pos.lower_bound(col);

            pos.erase(lb, ub);
            res.push_back(pos.size() - 1);
        }
        return res;


    }
};
