class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pair<int, int>> roads;

        for(int i=0; i < n - 1; i++) {
            roads.insert({i, i + 1});
        }
        vector<int> ans;
        for(auto query: queries) {
            auto it = roads.upper_bound({query[0], 1e9});
            if(it != roads.begin()) {
                it--;
                if(it->second >= query[1]) {
                    ans.push_back(roads.size());
                    continue;
                }
                else {
                    it++;
                    while(it != roads.end() && it->second <= query[1]) {
                        auto it2 = it;
                        it++;
                        roads.erase(it2);
                    }
                    ans.push_back(roads.size());
                    continue;
                }
            }
            // ans.push_back(roads.size());
        }
        return ans;
    }
};
