class Solution {
public:
    typedef pair<int, int> pii;
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pii> S;
        vector<int> res;
        for(int i = 0; i < n - 1; i ++ )    S.insert({i, i + 1});
        // cout << S.size() << endl;
        // cout << "---" << endl;
        int cur = n - 1;
        for(auto &item : queries){
            int x = item[0], y = item[1];
            auto it = S.lower_bound({x, -1});
            // if(it->first > x){
            //     res.push_back(cur);
            //     continue;
            // }
            int cnt = 0;
            for(;it != S.end();){
                if(it->second > y)  break;
                else{
                    cnt ++ ;
                    it = S.erase(it);
                }
            }
            if(cnt != 0){
                S.insert({x, y});
                cur = cur - cnt + 1;
            }
            res.push_back(cur);
            // cout << cnt << ' ' << S.size() << endl;
        }
        return res;
    }
};
