class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int>g;
        for(int i =0; i <n ; i++){
            g.insert(i);
        }
        vector<int>ret;
        for(auto qq: queries){
            int i = qq[0];
            int j = qq[1];
            auto left = g.upper_bound(i);

            if(!g.count(j)){
                ret.push_back(g.size()-1);
                continue;
            }
            // cout<<*left<<" "<<*right<<endl;
            // for(auto it = left; it <right ; ){
            //    it= g.erase(it);
            // }
            while(1){
                // cout<<*left<<" "<<j<<endl;
                if(*left==j)break;
                g.erase(left++);

            }
            ret.push_back(g.size()-1);
        }

        return ret;
    }
};
