class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> list(n,0);
        for(int i=1;i<n;i++){
            list[i] = list[i-1]+1;
        }
        set<int> st;
        for(int i=0;i<n;i++){
            st.insert(i);

        }
        vector<int> res;
        for(auto i :queries){
            int l = i[0];
            int j=i[1];
            auto lowerBound = st.lower_bound(l+1);
            auto upperBound = st.lower_bound(j);
            st.erase(lowerBound,upperBound);
            res.push_back(st.size()-1);
        }
        return res;
    }
};
