class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i=0;i<n;i++)
        {
            st.insert(i);
        }
        vector<int> ans;
        for(auto e:queries)
        {
            auto iter=st.upper_bound(e[0]);
            for(auto it=iter;it!=st.end();)
            {
                if(*it>e[0] && *it<e[1])
                {
                    // cout<<*it<<endl;
                    it=st.erase(it);
                }
                else
                {
                    break;
                }
            }
            ans.push_back(max(1,(int)st.size()-1));
        }
        return ans;
    }
};
