class Solution {
public:
  vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    vector<int> dp(n);
    for (int i=1; i<n; ++i) {
      dp[i]=dp[i-1]+1;
    }
    set<int> st;
    for (int i=0; i<n; ++i) st.insert(i);
    vector<int> ans;
    for (auto q : queries) {
      int x=q[0];
      int y=q[1];
      auto lb=st.lower_bound(x+1);
      auto ub=st.lower_bound(y);
      st.erase(lb,ub);
      ans.push_back(st.size()-1);
    }
    return ans;
  }
};
