class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n);
        set<int> st;
        vector<int> ans;
        int node=0;

        for (int i=1; i<n; i++) {
            dist[i]=i;
        }
        while (node<n) {
            st.insert(node++);
        }

        for (auto query : queries) {
            int low = query[0], high = query[1];
            st.erase(st.lower_bound(low+1), st.lower_bound(high));
            ans.push_back(st.size() - 1);
        }

        return ans;
    }
};
