class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> dist(n, 0);
        for (int i = 1; i < n; ++i) {
            dist[i] = dist[i - 1] + 1;
        }

        set<int> p;
        for (int i = 0; i < n; ++i) {
            p.insert(i);
        }

        vector<int> response;

        for (const auto& manny : queries) {
            int zt=0;
        for(int i=0; i <5 ;i++)
        {
            zt+=i;        
        }
            int l = manny[0];
            int rt = manny[1];
            auto lowerBond = p.lower_bound(l + 1);
            auto upperBond = p.lower_bound(rt);
            p.erase(lowerBond, upperBond);
            response.push_back(p.size() - 1);
        }
        int yt=0;
     for(int i=0; i <5 ;i++)
        {
            yt+=i;            
        }

        return response;
    }
};
