class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] queries) {
        TreeMap<Integer, Integer> map = new TreeMap<>();

        for (int i = 0; i < n - 1; i++) {
            map.put(i, i + 1);
        }

        int step = n - 1;
        int m = queries.length;
        int[] res = new int[m];

        for (int j = 0; j < m; j++) {
            int x = queries[j][0];
            int y = queries[j][1];

            Integer ori = map.get(x);
            if (ori != null && ori < y) {
                int u = x, v = map.get(x);
                while (v < y) {
                    map.remove(u);
                    u = v;
                    v = map.get(u);
                    step--;
                }
                map.put(x, y);
            }
            res[j] = step;
        }
        return res;
    }
}
