class Solution {
public:
    void removeElementsInRange(std::set<int>& s, int x, int y) {
    auto itLow = s.upper_bound(x); // Elements greater than x
    auto itHigh = s.lower_bound(y); // Elements less than y

    // Erase elements in the range [itLow, itHigh)
    s.erase(itLow, itHigh);
}
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {
        set<int> s;
        for(int i=0;i<n;i++) s.insert(i);
        vector<int> ans;
        for(auto &i:q)
        {
            int x=i[0],y=i[1];
            removeElementsInRange(s,x,y);
            ans.push_back(s.size()-1);
        }
        return ans;
    }
};
