class Solution 
{
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) 
    {
        vector<int> res;
        set<int> s;

        for(int i=0;i<n;i++)
        {
            s.insert(i);
        }

        for(int i=0;i<q.size();i++)
        {
            int x = q[i][0];
            int y = q[i][1];

            auto it1 = s.lower_bound(x + 1);
            auto it2 = s.lower_bound(y);

            if (it1 != s.end() && it2 != s.end() && it1 != it2) {
                s.erase(it1, it2);
            }

            res.push_back(s.size() - 1);
        }

        return res;
    }
};
