#define maxn 111111
int fa[maxn], s[maxn], m;

class Solution {
public:
    int find(int x) {
        m = 0;
        while (x != fa[x]) {
            s[m++] = x;
            x = fa[x];
        }
        for (int i = 0; i < m; ++i) {
            fa[s[i]] = x;
        }
        return x;
    }

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ret;
        for (int i = 0; i <= n; ++i) {
            fa[i] = i;
        }
        int x = n-1;
        for (auto e: queries) {
            int ct = 0;
            for (int i = find(e[0]); i < e[1]-1; i = find(i)) {
                fa[i] = i+1;
                ct += 1;
            }
            x -= ct;
            ret.push_back(x);
        }
        return ret;
    }
};
