class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> out;
        vector<bool> onIt(n, true);
        vector<int> far;
        for (int i = 0; i < n-1; ++i) {
            far.push_back(i+1);
        }
        int lastOut = n-1;
        for (int i = 0; i < queries.size(); ++i){
            int ui = queries[i][0], vi = queries[i][1];
            if (!onIt[ui]) {
                out.push_back(lastOut);
                continue;
            }
            int count = 0, j = far[ui];
            while (j < vi) {
                onIt[j] = false;
                ++count;
                j = far[j];
            }
            far[ui] = vi;
            lastOut -= count;
            out.push_back(lastOut);
        }
        return out;
    }
};
