class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& q) {

        int m = q.size() , ans = n-1 , cur=0,x,y,i,j,last;

        set<pair<int , int>> s;
        for(i = 0;i< n-1;i++)
            s.insert({i , i+1});

        vector<pair<int , int>> temp;
        vector<int> a(m) , check(n , 0);
        for(i = 0;i< m;i++){
            temp.resize(0);
            x = q[i][0] , y = q[i][1] , cur = 0;
            if (check[x] == 1){
                a[i] = ans;
                continue;
            }

            auto itt = s.lower_bound({x , numeric_limits<int>::min()});

            for(auto it = itt; it != s.end();it++) {
                // if (i == m-1){
                //     cout << it->first << " " << it->second << endl;
                // }
                if (it->first >= y)
                    break;

                temp.push_back({it->first , it->second});
            }
            cur = temp.size();

            if (cur > 0){
                s.insert({x , y});
            }

            ans = ans - cur + 1;
            a[i] = ans;

            // cout << cur << " ";

            last = x;
            for(auto it : temp){
                for(j = last;j< it.first;j++)
                    check[j] = 1;

                if (x < it.first && y > it.first)
                    check[it.first] = 1;
                if (x < it.second && y > it.second)
                    check[it.second] = 1;

                s.erase({it.first , it.second});
                last = it.second;
                // if (it.first+1 == it.second)
                //     check[x] = 1;

                // for(j = 0;j< n;j++)
                //     cout << check[j] << " ";
                // cout << endl;
            }

        }




        return a;
    }
};
