#include <vector>
#include <set>

class Solution {
public:
    std::vector<int> shortestDistanceAfterQueries(int n, std::vector<std::vector<int>>& queries) {
        std::vector<int> distances(n, 0);
        for (int i = 1; i < n; ++i) {
            distances[i] = distances[i - 1] + 1;
        }

        std::set<int> positions;
        for (int i = 0; i < n; ++i) {
            positions.insert(i);
        }

        std::vector<int> result;

        for (const auto& query : queries) {
            int left = query[0];
            int right = query[1];

            auto lower_bound = positions.lower_bound(left + 1);
            auto upper_bound = positions.lower_bound(right);

            positions.erase(lower_bound, upper_bound);

            result.push_back(positions.size() - 1);
        }

        return result;
    }
};
