class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int _total_nodes_, vector<vector<int>>& _queries_list_) {
        vector<int> _path_lengths_(_total_nodes_, 0);
        for (int _index_ = 1; _index_ < _total_nodes_; ++_index_) {
            _path_lengths_[_index_] = _path_lengths_[_index_ - 1] + 1;
        }

        set<int> _active_positions_;
        for (int _index_ = 0; _index_ < _total_nodes_; ++_index_) {
            _active_positions_.insert(_index_);
        }

        vector<int> _query_results_;

        for (const auto& _query_pair_ : _queries_list_) {
            int _start_pos_ = _query_pair_[0];
            int _end_pos_ = _query_pair_[1];

            auto _start_iter_ = _active_positions_.lower_bound(_start_pos_ + 1);
            auto _end_iter_ = _active_positions_.lower_bound(_end_pos_);

            _active_positions_.erase(_start_iter_, _end_iter_);

            _query_results_.push_back(_active_positions_.size() - 1);
        }

        return _query_results_;
    }
};
