class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> ans;
        map<int,int> m;
        for(int i=0;i<n-1;i++){
            m[i]++;
        }
        for(auto p: queries){
            int a=p[0];
            int b=p[1];
            auto it=m.lower_bound(a+1);
            if(it==m.end()){
                ans.push_back(m.size());
                continue;
            }
            else{
                while(it!=m.end() && it->first<b){
                    int x=it->first;
                    ++it;
                    m.erase(x);

                }
                ans.push_back(m.size());
            }
        }
        return ans;
    }
};
