class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        adj = {i:i+1 for i in range(n-1)}
        ans = [] #[]

        for i, j in queries:
            # currx, curry = i, adj[i] 1,2
            if i in adj:
                curry = adj[i]
                while curry < j: 
                    if curry in adj: 
                        del adj[curry]
                    curry += 1
                        # newy = adj[curry]
                        # del adj[curry]
                        # curry = newy
                adj[i] = j 

            ans.append(len(adj))

        return ans
