using namespace std;
// using LL = __int128;
using uint = unsigned int;
using ll = long long;
using ull = unsigned long long;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for (int i = 0; i < n - 1; i++) {
            st.emplace(i);
        }
        int q = (int)queries.size();
        vector<int> ans(q);
        for (int i = 0; i < q; i++) {
            int from = queries[i][0], to = queries[i][1];
            auto it = st.lower_bound(from + 1);
            while (it != st.end() && *it < to) {
                st.erase(it);
                it = st.lower_bound(from + 1);
            }
            ans[i] = (int)st.size();
        }
        return ans;
    }
};
