#include <vector>
#include <set>
using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        // Initialize the distance array where each city initially connects to the next city
        vector<int> d(n, 0);
        for (int i = 1; i < n; ++i) {
            d[i] = d[i - 1] + 1;
        }

        // Set to maintain the cities, initially contains all cities
        set<int> p;
        for (int i = 0; i < n; ++i) {
            p.insert(i);
        }

        // Result vector to store the shortest path length after each query
        vector<int> result;

        // Process each query
        for (const auto& query : queries) {
            int l = query[0];
            int r = query[1];

            // Find the range of cities to remove from the set
            auto lb = p.lower_bound(l + 1);
            auto ub = p.lower_bound(r);

            // Remove the cities in the range (l+1, r)
            p.erase(lb, ub);

            // The shortest path length is the size of the set minus 1
            result.push_back(p.size() - 1);
        }

        return result;
    }
};
