class Solution {
public:
    using T=vector<int>;
    using G=vector<T>;
    using P=pair<int, int>;
    using S=set<int>;

    const int MAX=numeric_limits<int>::max();

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        S st;
        for (int i=0; i<n; ++i) st.insert(i);

        T r;
        for (const auto& q : queries) {
            int x = q[0];
            int y = q[1];

            auto it1 = st.find(x);
            auto it2 = st.find(y);

            if (it1 != st.end() && it2 != st.end()) {
                auto it = next(it1);
                while (it != st.end() && it != it2) {
                    it = st.erase(it);
                }
            } 
            r.push_back(st.size()-1);
        }
        return r;
    }
};
