class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> res;
        set<int> s;

        for(int i = 0; i < n; i++) {
            s.insert(i);
        }

        for(vector<int> query : queries) {
            int u = query[0];
            int v = query[1];

            s.erase(s.lower_bound(u+1), s.lower_bound(v));
            int size = s.size();
            res.push_back(size - 1);
        }

        return res;

    }
};
