//======================================================================================
#define x first
#define y second
typedef long long ll;
typedef pair<int, int> pii;


class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<pii> st;
        int ans = n - 1;
        vector<int> anss;
        for(vector<int> q: queries) {
            int x = q[0];
            int y = q[1];
            //cout << "x: " << x << ' ' << "y: " << y <<endl;
            st.insert({x, y});
            ans -= y - x - 1;
            auto it = st.find({x, y});
            it ++ ;
            if (it != st.end()){
                pii ne = *it;
                //cout << ne.x << ' ' << ne.y << endl; 
                if(ne.x >= x && ne.y <= y) {
                    ans += ne.y - ne.x - 1;
                    st.erase(ne);
                } else if (ne.x <= x && ne.y >= y) {
                    ans += y - x - 1;
                    st.erase({x, y});
                    anss.push_back(ans);
                    continue;
                }
            }

            it = st.find({x, y});
            if (it != st.begin()) {
                it -- ;
                pii pre = *it;
                //cout << pre.x << ' ' << pre.y << endl; 
                if(pre.x == x && pre.y <= y) {
                    ans += pre.y - pre.x - 1;
                    st.erase(pre);
                }else if (pre.x <= x && pre.y >= y) {
                    ans += y - x - 1;
                    st.erase({x, y});
                    anss.push_back(ans);
                    continue;
                }
            }
            anss.push_back(ans);
        }
        return anss;
    }
};
