class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qs) {
        set<int> se;
        for (int i = 0; i < n; i++) {
            se.insert(i);
        }
        vector<int> ans;

        for (auto&& q : qs) {
            int u = q[0], v = q[1];
            auto it = se.upper_bound(u);
            while (*it < v) {
                it = se.erase(it);
            }
            ans.push_back(se.size() - 1);
        }
        return ans;
    }
};
