from sortedcontainers import SortedSet
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        points = SortedSet([i for i in range(n)])
        res = []
        for u, v in queries:
            a, b = points.bisect_left(u), points.bisect_left(v)
            todo = [points[i] for i in range(a+1, b)]
            for i in todo:
                points.remove(i)
            res.append(len(points)-1)
        return res
