class Solution {
    int id[100005];
    int d[100005];
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int m=queries.size();
        vector<int> res(m);
        for(int i=0;i<=n;++i)
		    id[i]=d[i]=i;
        int u,v,t,cnt=n-1,u0;
        for(int i=0;i<m;++i)
        {
            u0=u=queries[i][0];    v=queries[i][1];
            // cout<<u<<","<<v<<" # "<<d[u]<<" "<<d[v]<<endl;
            if(d[u]>=d[v]){
                res[i]=cnt;
                continue;
            }
            while(u<v){
                t=d[u];
                if(t!=u){
                    u=t;
                }else{
                    u=t+1;
                }
                --cnt;
                // cout<<u0<<" "<<u<<" "<<cnt<<endl;
                if(t>=v){
                    break;
                }
            }
            // d[u0]=v;
            ++cnt;
            res[i]=cnt;
            u=u0;
            while(u<v){
                t=d[u];
                d[u]=v;
                if(t==u){
                    u=t+1;
                }else{
                    u=t;
                }
            }
        }

        return res;
    }

    int Find(int x)
    {
        if(id[x]!=x)
            id[x]=Find(id[x]);
        return id[x];
    }

    void Union(int a,int b)
    {
        id[Find(a)]=Find(b);
    }
};
