class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        set<int> st;
        for(int i = 0; i < n; i++) st.insert(i);
        vector<int> ans;
        for(auto i : queries) {
            auto lf = st.find(i[0]);
            auto rg = st.find(i[1]);
            if(rg != st.end() && lf != st.end()) {
                lf++;
                st.erase(lf, rg);
            }
            ans.push_back(st.size() - 1);
        }
        return ans;
    }
};
