class Solution {
    public int[] shortestDistanceAfterQueries(int n, int[][] qu) {



        int size = n;
        int[] next  = new int[n];
        int m = qu.length;
        int[] res = new int[m];

        for(int i = 0;i<n;i++) next[i] = i+1;

        int[] rem = new int[n];
        int j = 0;
        for(int[] q : qu){
            int i = q[0];
            if(rem[i] == 0){
            while(i < n && i < q[1]){
                i = next[i];
                if(i > q[0] && i < q[1] && rem[i] == 0){
                    rem[i] = 1;
                    size--;
                }
            }
            next[q[0]] = Math.max(q[1],next[q[0]]);
            // System.out.println(Arrays.toString(rem));
            }
            res[j++] = size-1;
        }

        return res;

    }
}
