from sortedcontainers import SortedList
class Solution:
    def shortestDistanceAfterQueries(self, n: int, queries: List[List[int]]) -> List[int]:
        m = len(queries)
        ans = []
        g = SortedList([i for i in range(n)])
        for v, u in queries:
            a = g.bisect_left(v)
            if g[a] == v:
                a = a + 1

                while g[a] < u:
                    g.pop(a)
            ans.append(len(g) - 1)
            #print(f'dist:{dist},g:{g}')
        return ans
