#include <vector>
#include <set>
#include <algorithm>

using namespace std;

class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int cities, vector<vector<int>>& queries) {
        // Distance initialization
        vector<int> dist(cities, 0);
        for (int i = 1; i < cities; ++i) {
            dist[i] = dist[i - 1] + 1;
        }

        // Set of positions
        set<int> positions;
        for (int i = 0; i < cities; ++i) {
            positions.insert(i);
        }

        vector<int> results;

        for (const auto& query : queries) {
            int start = query[0];
            int end = query[1];

            auto lower = positions.lower_bound(start + 1);
            auto upper = positions.lower_bound(end);

            positions.erase(lower, upper);

            results.push_back(positions.size() - 1);
        }

        return results;
    }
};
