class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        unordered_map<int, int> adj;
        for(int i = 0; i < n - 1; i++)
        {
            adj[i] = i + 1;
        }
        vector<int> ans;
        for(auto &vec : queries)
        {
            int u = vec[0], v = vec[1];
            if(adj.count(u))
            {
                if(v > adj[u])
                {
                    for(int i = adj[u]; i < v; i++)
                        if(adj.count(i)) adj.erase(i);
                    adj[u] = v;
                }
            }
            ans.push_back(adj.size());
        }
        return ans;
    }
};
