class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        vector<int> adj(n, 0);
        for (int i = 1; i < n; i++) {
            adj[i] = adj[i-1]+1;
        }

        set<int> st;
        for(int i=0; i<n; i++) st.insert(i);

        vector<int> ans;
        for (const auto& query : queries) {
            int u = query[0];
            int v = query[1];
            auto lb = st.lower_bound(u+1);
            auto ub = st.lower_bound(v);
            st.erase(lb, ub);
            ans.push_back(st.size()-1);
        }
        return ans;
    }
};
