class Solution {
    set<pair<int, int>> st;
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& qs) {
        vector<int> ans;
        int cur = n - 1;
        for (auto& q : qs)
        {
            int l = q[0], r = q[1];
            auto it = st.lower_bound({l, -1}); 
            if (it != st.begin() && prev(it)->second >= r || it != st.end() && it->first == l && it->second >= r)
            {
                ans.push_back(cur);
                continue;
            }
            while (it != st.end() && it->second <= r)
            {
                cur -= 1;
                cur += it->second - it->first;
                auto pre = it;
                it = next(it);
                st.erase(pre);
            }    
            st.insert({l, r});
            cur -= r - l;
            cur += 1;
            ans.push_back(cur);
        }
        return ans;
    }
};
