int p[100010],sz[100010];
class Solution {
public:
    int find(int x){
        if(p[x]!=x) p[x]=find(p[x]);
        return p[x];
    }

    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
        int tans=n-1;
        vector<int> ans;
        for(int i=0;i<n-1;i++) p[i]=i,sz[i]=1;
        for(int i=0;i<queries.size();i++){
            int l=queries[i][0],r=queries[i][1];
            int pl=find(l);
            if(sz[pl]>r-l) ans.push_back(tans);
            else{
                while(sz[l]<r-l){
                    int ne=l+sz[l];
                    p[ne]=p[l];
                    sz[l]+=sz[ne];
                    tans--;
                }
                ans.push_back(tans);
            }
        }
        return ans;
    }
};
