class Solution {
public:
    vector<int> shortestDistanceAfterQueries(int n, vector<vector<int>>& queries) {
    vector<int> dist(n, 0);
    set<int> indices;
    for (int idx = 1; idx < n; ++idx) dist[idx] = dist[idx - 1] + 1;

    for (int idx = 0; idx < n; ++idx) indices.insert(idx);
    vector<int> ans;
    for (int i =0; i< queries.size(); i++) {
        int start = queries[i][0];
        int end = queries[i][1];
        indices.erase(indices.lower_bound(start + 1), indices.lower_bound(end));
        ans.push_back(indices.size() - 1);
    }
    return ans;
    }
};
